mapper.select:
SELECT id, pwd, profile, email, regdate, Name, SVC_USE_PCY_AGMT_YN, PS_INFO_PROC_AGMT_YN, MKT_INFO_RECV_AGMT_YN, social
FROM users
WHERE id = #{id}

SELECT id, pwd, profile, email, regdate, Name, SVC_USE_PCY_AGMT_YN, PS_INFO_PROC_AGMT_YN, MKT_INFO_RECV_AGMT_YN, social
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
SET social = 2
WHERE id = #{id}

UPDATE users
SET pwd = #{newPwd}
WHERE id = #{id}

UPDATE users
SET email = #{email}, profile= #{profile}
WHERE id = #{id}

